import React, { useState } from "react";
import Profile from "./../../Assets/1611841006727.jpeg";
import style from "./Layout.module.css";
import { Outlet, NavLink } from "react-router-dom";

const Layout = () => {
  const [activeClass, setActiveClass] = useState("");

  function handleActiveClass() {
    setActiveClass("style.active");
  }

  return (
    <div className={style.wrap}>
      <nav className={style.navbar}>
        <div className={style.leftNavBar}>
          <div className={style.profileImg}>
            <NavLink to="/home">
              <img src={Profile}></img>
            </NavLink>
          </div>
          <ul className={`${style.navList}`}>
            <li>
              <NavLink
                to="/home"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {};
                }}
              >
                <span>home</span>
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/about"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {
                        background: "rgba(3, 127, 255, 0.2)",
                        content: "",
                        height: "100%",
                        left: "0",
                        position: "absolute",
                        right: "100%",
                        top: "0",
                        transition: " all 0.4s ease-out",
                        visibility: "hidden",
                        width: "0",
                        //zIndex: "-1",
                      };
                }}
              >
                About
              </NavLink>
            </li>
          </ul>
        </div>
      </nav>
      <Outlet />
    </div>
  );
};

export default Layout;
