import React from "react";
import Profile from "./../../Assets/1611841006727.jpeg";
import style from "./Layout.module.css";
import { Outlet, NavLink } from "react-router-dom";

const Layout = () => {
  const activeState = ({ isActive }) => {
    return {
      color: isActive ? "rgb(253 230 138)" : "",
      backgroundColor: isActive ? "rgb(69 26 3)" : "",
      fontWeight: isActive ? "bold" : "",
    };
  };
  return (
    <div className={style.wrap}>
      <nav className={style.navbar}>
        <div className={style.leftNavBar}>
          <div className={style.profileImg}>
            <NavLink to="/home">
              <img src={Profile}></img>
            </NavLink>
          </div>
          <ul className={`${style.navList}`}>
            <li>
              <NavLink
                to="/home"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {};
                }}
              >
                <span>home</span>
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/about"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {};
                }}
              >
                About
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/Resume"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {};
                }}
              >
                Resume
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/Contact"
                style={({ isActive }) => {
                  return isActive
                    ? {
                        color: "#fff",
                        background: "#037fff",
                        visibility: "visible",
                        width: "100%",
                      }
                    : {};
                }}
              >
                Contact
              </NavLink>
            </li>
          </ul>
        </div>
      </nav>
      <Outlet />
    </div>
  );
};

export default Layout;
